import React, {useState, useEffect} from 'react';
import axios from 'axios';

export const Article = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/article/all')
      .then(res => {
        setArticles(res.data); // Save articles to state
      })
      .catch(err => {
        console.error('Error fetching articles:', err);
      });
  }, []);

  return (
    <div>
        <h1>Article</h1>
        <p>This is the article page.</p>
        <p>Here you can read articles about agriculture, farming techniques, and more.</p>
        <p>Stay tuned for more updates!</p>

        <div>
            <h2>Article Title</h2>
            <p>Author: John Doe</p>
            <p>Date: 2023-10-01</p>
            <img src="https://via.placeholder.com/600x400" alt="Article Image" />
            <p>This is the content of the article. It contains valuable information about agriculture.</p>
        </div>

    </div>
  )
}
