import React, {useState, useEffect} from 'react';
import axios from 'axios';


export const Article = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/article/getAll')
      .then(res => {
        setArticles(res.data); // Save articles to state
      })
      .catch(err => {
        console.error('Error fetching articles:', err);
      });
  }, []);

  return (
    <div>
        <h1>Article</h1>
        <p>This is the article page.</p>
        <p>Here you can read articles about agriculture, farming techniques, and more.</p>
        <p>Stay tuned for more updates!</p>

        <h2>All Articles</h2>
          {articles.length === 0 ? (
            <p>No articles found.</p>
          ) : (
            articles.map((article, index) => (
              <div key={index} className="card mb-3">
                <img src={article.image} alt="Article" className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">{article.title}</h5>
                  <p className="card-text">{article.content}</p>
                </div>
              </div>
            ))
          )}

    </div>
  )
}
