import axios from 'axios';
import React, { useState } from 'react';
import "../styles/CreateArticle.css";
import "bootstrap/dist/css/bootstrap.min.css";

const CreateArticle = () => {
  const [title, setTitle] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [content, setContent] = useState("");
  const [isUploading, setIsUploading] = useState(false);

  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Just store file, don't upload yet
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!imageFile) {
      alert("Please select an image to upload");
      return;
    }

    try {
      setIsUploading(true);

      // 1. Upload the image to Cloudinary
      const formData = new FormData();
      formData.append("image", imageFile);

      const uploadRes = await axios.post("http://localhost:5000/api/article/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      const imageUrl = uploadRes.data.imageUrl;

      // 2. Submit article data with uploaded image URL
      const response = await axios.post("http://localhost:5000/api/article/create", {
        title,
        content,
        image: imageUrl,
      });

      console.log(response.data);
    //   alert("Article Created Successfully");

      // Clear the form
      setTitle("");
      setContent("");
      setImageFile(null);
      e.target.reset();
    } catch (error) {
      console.error("Error:", error.message);
      alert("Failed to create article");
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <>
     <div className='container create-article'>
      <div className="row">
        <div className=''>

        </div>
      </div>
     </div>
    </>
  );
};

export default CreateArticle;
