import React, {useState, useEffect} from 'react';
import axios from 'axios';
import "../styles/Article.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {Link} from 'react-router-dom';

export const Article = () => {
  const [articles, setArticles] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");


  useEffect(() => {
    axios.get('http://localhost:5000/api/article/getAll')
      .then(res => {
        setArticles(res.data); // Save articles to state
      })
      .catch(err => {
        console.error('Error fetching articles:', err);
      });
  }, []);

  return (
    <div className="container-fluid ">
      <div className="row">
        <h2 className='text-primary text-center'>Latest Articles</h2>
        <input
          type="text"
          placeholder="Search articles..."
          className="form-control mb-4"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        
          {articles.length === 0 ? (
            <p>No articles found.</p>
          ) : (
        articles
          .filter((article) =>
            article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
            article.content.toLowerCase().includes(searchTerm.toLowerCase())
          )
          .map((article, index) => (
            <div key={index} className="card shadow-sm col-sm-12 col-md-4 col-lg-3 mb-4">
              {/* existing article card content */}
            </div>
          ))
          )}

      </div>
        

        

    </div>
  )
}
