import React, {useState, useEffect} from 'react';
import axios from 'axios';
import "../styles/Article.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {Link} from 'react-router-dom';
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export const Article = () => {
  const [articles, setArticles] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");


  useEffect(() => {
    axios.get('http://localhost:5000/api/article/getAll')
      .then(res => {
        setArticles(res.data); // Save articles to state
      })
      .catch(err => {
        console.error('Error fetching articles:', err);
      });
  }, []);

  const SingleArticle = ({ article }) => {
    const { user } = useContext(AuthContext);
    const [reactionCount, setReactionCount] = useState(article.reactions.length || 0);
    const [reacted, setReacted] = useState(article.reactions?.includes(user?._id));

    const handleReaction = async () => {
      try {
        const res = await axios.post(`http://localhost:5000/api/article/react/${article._id}`, {}, {
          headers: {
            Authorization: `Bearer ${user.token}`
          }
        });

        setReactionCount(res.data.reactions);
        setReacted(!reacted);
      } catch (err) {
        alert("You must login to react!");
      }
    };
  



  return (
    <div className="container-fluid ">
      <div className="row">
        <h2 className='text-success text-center mb-4'>Latest Articles</h2>
        <input
          type="text"
          placeholder="üîç Search articles..."
          className="form-control mb-4"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        
        {articles.length === 0 ? (
          <p>No articles found.</p>
        ) : (
          articles
            .filter((article) =>
              article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
              article.content.toLowerCase().includes(searchTerm.toLowerCase())
            ).map((article, index) => (
            <div key={index} className="card shadow-sm col-sm-12 col-md-4 col-lg-3 mb-4">
                <div className='card-components '>
                  <img src={article.image} alt={article.title} className="card-img-top" />
                  <div className="card-body d-flex flex-column justify-content-center ">
                    <h5 className="card-title text-center">{article.title}</h5>
                    <p className="card-text preview-multiline">{article.content}</p>
                    <Link to={`/article/${article._id}`} className="btn btn-success">Read More</Link>
                    {user && (
                      <button className={`btn ${reacted ? 'btn-danger' : 'btn-outline-danger'}`} onClick={handleReaction}>
                        ‚ù§Ô∏è {reactionCount}
                      </button>
                    )}

                  </div>
                </div>
            </div>
          ))
        )}

      </div>
    </div>
  )
}
}
