export const Article = () => {
  const [articles, setArticles] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const { user } = useContext(AuthContext); // ✅ You missed this
  const axios = require('axios');
  const { useEffect } = require('react');
  const { SingleArticle } = require('../components/SingleArticle'); // ✅ You missed this
  const { AuthContext } = require('../context/AuthContext'); // ✅ You missed this
  const { useContext } = require('react'); // ✅ You missed this
  const { useState } = require('react'); // ✅ You missed this

  useEffect(() => {
    axios.get('http://localhost:5000/api/article/getAll')
      .then(res => {
        setArticles(res.data);
      })
      .catch(err => {
        console.error('Error fetching articles:', err);
      });
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">
        <h2 className='text-success text-center mb-4'>Latest Articles</h2>
        <input
          type="text"
          placeholder="🔍 Search articles..."
          className="form-control mb-4"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        {articles.length === 0 ? (
          <p>No articles found.</p>
        ) : (
          articles
            .filter((article) =>
              article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
              article.content.toLowerCase().includes(searchTerm.toLowerCase())
            ).map((article, index) => (
              <SingleArticle key={index} article={article} user={user} />
            ))
        )}
      </div>
    </div>
  );
};
